<?phpclass LOCATION_GEO_Activator {	public static function activate() {        if (function_exists( 'run_location_geocentric' )) {            deactivate_plugins(plugin_basename(__FILE__));            wp_die(__(' Please deactivate Rank Forest Plugin', 'location-geo'));        }        global $wpdb;        $plugin_name_db_version = '1.0';        $table_name = $wpdb->prefix . "location_geo_options";        $charset_collate = $wpdb->get_charset_collate();        $sql = "CREATE TABLE $table_name (                id mediumint(9) NOT NULL AUTO_INCREMENT,                created timestamp NOT NULL default CURRENT_TIMESTAMP,                name tinytext NULL,                value BLOB DEFAULT '' NOT NULL,                UNIQUE KEY id (id)                ) $charset_collate;";        require_once( ABSPATH . 'wp-admin/includes/upgrade.php' );        dbDelta( $sql );        $wpdb->query(            $wpdb->prepare(                "CREATE TABLE IF NOT EXISTS {$wpdb->prefix}shortcode_requests (                    id INT(6) UNSIGNED AUTO_INCREMENT PRIMARY KEY,                    shortcode VARCHAR(10) NOT NULL,                    city VARCHAR(10) NOT NULL,                    result VARCHAR(1000) NOT NULL,                    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,                    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP                  )" . $wpdb->get_charset_collate()            )        );        add_option( 'Location_Geo_db_version', $plugin_name_db_version );        $options = array();        $options['max_sb_location_geo_neighborhood_location'] = 'Baton Rouge, Louisiana';        $options['max_sb_location_geo_neighborhood_limit'] = 100;        $options['max_sb_location_geo_neighborhood_sort'] = 'asc';        $options['max_sb_location_geo_activities_location'] = 'Baton Rouge, Louisiana';        $options['max_sb_location_geo_activities_limit'] = 100;        $options['max_sb_location_geo_activities_sort'] = 'asc';        $options['max_sb_location_geo_activities_types'] = 'park,restaurant,stadium,tourist_attraction,point_of_interest,landmark';        $options['max_sb_location_geo_activities_radius'] = 500;        $options['max_sb_location_geo_locations_sentence'] = 10;        $options['max_sb_location_geo_gmap_location'] = 'Baton Rouge, Louisiana';        $options['max_sb_location_geo_gmap_radius'] = 100;        $options['max_sb_location_geo_gmap_height'] = 500;        $options['max_sb_location_geo_weather_api_key'] = '9330008cc69b4b6b15c065bc50d8267f';        $id = "max_sb_location_geo_settings";        $option_exists = (get_option($id, null) !== null);        if (!$option_exists) {            update_option( 'max_sb_location_geo_settings', $options );        }	}}