<?phpfunction max_sb_location_geo_settings_init() {    register_setting( 'max_sb_location_geo', 'max_sb_location_geo_options' );    register_setting( 'max_sb_location_geo_settings', 'max_sb_location_geo_settings' );    max_section_design();    max_fields_design();    max_section_settings();    max_fields_settings();}add_action( 'admin_init', 'max_sb_location_geo_settings_init' );function max_section_design(){    add_settings_section(        'max_sb_location_geo_design_styling',        __( 'Styling', 'max_sb_location_geo' ), 'max_sb_location_geo_section_styling_data',        'max_sb_location_geo'    );}function max_section_settings(){    add_settings_section(        'max_sb_location_geo_settings_neighborhood',        __( 'Neighborhood', 'max_sb_location_geo' ), 'max_sb_location_geo_settings_neighborhood_data',        'max_sb_location_geo_settings'    );    add_settings_section(        'max_sb_location_geo_settings_activities',        __( 'Activities', 'max_sb_location_geo' ), 'max_sb_location_geo_settings_activities_data',        'max_sb_location_geo_settings'    );    add_settings_section(        'max_sb_location_geo_settings_location',        __( 'Location Information', 'max_sb_location_geo' ), 'max_sb_location_geo_settings_location_data',        'max_sb_location_geo_settings'    );    add_settings_section(        'max_sb_location_geo_settings_map',        __( 'Google Map', 'max_sb_location_geo' ), 'max_sb_location_geo_settings_map_data',        'max_sb_location_geo_settings'    );    add_settings_section(        'max_sb_location_geo_settings_api',        __( 'Google Map API', 'max_sb_location_geo' ), 'max_sb_location_geo_settings_api_data',        'max_sb_location_geo_settings'    );    add_settings_section(        'max_sb_location_geo_settings_weather_api',        __( 'Open Weather Map Api', 'max_sb_location_geo' ), 'max_sb_location_geo_settings_weather_api_data',        'max_sb_location_geo_settings'    );    add_settings_section(        'max_sb_location_geo_settings_license_key',        __( 'License Key', 'max_sb_location_geo' ), 'max_sb_location_geo_settings_license_key_data',        'max_sb_location_geo_settings'    );}function max_sb_location_geo_settings_neighborhood_data( $args ) {    ?>    <p id="<?php echo esc_attr( $args['id'] ); ?>">        <?php esc_html_e( 'This are the default options applied to all shortcodes. Defining the values directly in the shortcode will override the values define on this page.', 'max_sb_location_geo' ); ?>    </p>    <?php}function max_sb_location_geo_settings_activities_data( $args ) {    ?>    <p id="<?php echo esc_attr( $args['id'] ); ?>">        <?php esc_html_e( 'This are the default options applied to all shortcodes. Defining the values directly in the shortcode will override the values define on this page.', 'max_sb_location_geo' ); ?>    </p>    <?php}function max_sb_location_geo_settings_location_data( $args ) {    ?>    <p id="<?php echo esc_attr( $args['id'] ); ?>">        <?php esc_html_e( 'This are the default options applied to all shortcodes. Defining the values directly in the shortcode will override the values define on this page.', 'max_sb_location_geo' ); ?>    </p>    <?php}function max_sb_location_geo_settings_map_data( $args ) {    ?>    <p id="<?php echo esc_attr( $args['id'] ); ?>">        <?php esc_html_e( 'This are the default options applied to all shortcodes. Defining the values directly in the shortcode will override the values define on this page.', 'max_sb_location_geo' ); ?>    </p>    <?php}function max_sb_location_geo_settings_api_data( $args ) {    ?>    <p id="<?php echo esc_attr( $args['id'] ); ?>">        <?php esc_html_e( 'Add your google map api key.', 'max_sb_location_geo' ); ?>    </p>    <?php}function max_sb_location_geo_settings_weather_api_data( $args ) {    ?>    <p id="<?php echo esc_attr( $args['id'] ); 