page=dmn8-geo-location&tab=help" class="nav-tab <?php if($tab==='help'):?>nav-tab-active<?php endif; ?>">Help</a>        </nav>        <div class="tab-content">            <?php                switch($tab) :                case 'settings':                    max_settings_tab();                    break;                case 'design':                    max_design_tab();                    break;                case 'help':                    max_help_tab();                    break;                default:                    max_dashboard_tab();                    break;                endswitch;            ?>        </div>    </div>    <?php}function max_help_tab(){?>    <div class="max-sb-admin-tab">        <h4>For plugin documentaion and setting help please.</h4>        <p><a href="<?php echo plugin_dir_url(__FILE__) ?>../geo-location-installation.docx">Click Here</a></p>    </div><?php}function max_design_tab(){?>    <form action="options.php" method="post">        <?php        settings_fields( 'max_sb_location_geo' );        do_settings_sections( 'max_sb_location_geo' );        submit_button( 'Save Settings' );        ?>    </form><?php}function max_settings_tab(){    ?>    <form action="options.php" method="post">        <?php            settings_fields( 'max_sb_location_geo_settings' );            do_settings_sections( 'max_sb_location_geo_settings' );            submit_button( 'Save Settings' );        ?>    </form>    <?php}function max_dashboard_tab(){    ?>    <div class="max-sb-admin-tab">        <h2>Shortcodes</h2>        <h4>Details of a Location (city).</h4>        <pre><code>[max-details title="About New York City" location="New York City, New York" limit="10"]</code></pre>        <h4>List of Neighborhoods.</h4>        <pre>            <code>[max-neighborhoods title="New York" location="New York City" limit="30" sort="desc"]</code>        </pre>        <h4>List of Activities in a Locati