<?phpclass LOCATION_GEO_Public {	/**	 * The Name of plugin.	 *	 */	private $plugin_name;	/**	 * The Version of plugin.	 *	 */	private $version;    private $apiKey = 'AIzaSyALst4KZXiEiUNL66XkCajtTyfgebVyAA8';	public function __construct( $plugin_name, $version ) {        $settingsOptions = get_option( 'max_sb_location_geo_settings' );		$this->plugin_name = $plugin_name;		$this->version = $version;        $this->apiKey = (!empty($settingsOptions['max_sb_location_geo_gmap_api'])) ? $settingsOptions['max_sb_location_geo_gmap_api'] : '';        $this->weatherApiKey = (!empty($settingsOptions['max_sb_location_geo_weather_api_key'])) ? $settingsOptions['max_sb_location_geo_weather_api_key'] : '';	}	/**	 *Enque stylesheet.	 *	 */	public function enqueue_styles() {		wp_enqueue_style( $this->plugin_name, plugin_dir_url( __FILE__ ) . 'css/frontend.css', array(), rand(1, 100), 'all' );        wp_dequeue_style( 'location-geocentric' );        wp_deregister_style( 'location-geocentrics' );	}	/**	 * Register the JavaScript.	 *	 */	public function enqueue_scripts() {        $settingsOptions = get_option( 'max_sb_location_geo_settings' );        $this->weatherApiKey = $settingsOptions['max_sb_location_geo_weather_api_key'];        wp_dequeue_script('location-geocentric-gmap');        wp_deregister_script('location-geocentric-gmap');        wp_dequeue_script('location-geocentric-tz');        wp_deregister_script('location-geocentric-tz');        wp_dequeue_script('location-geocentric-lscache');        wp_deregister_script('location-geocentric-lscache');        wp_dequeue_script('location-geocentric-nicescroll');        wp_deregister_script('location-geocentric-nicescroll');        wp_dequeue_script('location-geocentric-places');        wp_deregister_script('location-geocentric-places');        wp_dequeue_script('location-geocentric-cluster');        wp_deregister_script('location-geocentric-cluster');        ?>        <script>            var weather_api = '<?php echo $this->weatherApiKey ?>';        </script>        <?php        wp_register_script( $this->plugin_name . '-max-jquery', plugin_dir_url( __FILE__ ) . 'js/jquery.js', array( 'jquery' ), $this->version , true );        wp_register_script( $this->plugin_name . '-googlemap', 'https://maps.googleapis.com/maps/api/js?key='.$this->apiKey.'&libraries=places', array(), false, true );        wp_register_script( $this->plugin_name . '-max-cluster-marker', plugin_dir_url( __FILE__ ) . 'js/max-marker.js', array( 'jquery' ), $this->version, true );        wp_register_script( $this->plugin_name . '-max-regions', plugin_dir_url( __FILE__ ) . 'js/max-regions.js', array( 'jquery' ), $this->version, true );        wp_register_script( $this->plugin_name . '-max-localcache', plugin_dir_url( __FILE__ ) . 'js/max-localcache.js', array( 'jquery' ), $this->version, true );        wp_register_script( $this->plugin_name . '-max-scrolanimation', plugin_dir_url( __FILE__ ) . 'js/max-scrolanimation.js', array( 'jquery' ), $this->version, true );        wp_register_script( $this->plugin_name . '-max-frontend', plugin_dir_url( __FILE__ ) . 'js/frontend.js', array( 'jquery' ), $this->version , true );        wp_register_script( $this->plugin_name . '-max-frontend-airport', plugin_dir_url( __FILE__ ) . 'js/frontend-airport.js', array( 'jquery' ), $this->version , true );    }	/**	 * Callback function.	 *	 */	public function max_geomap_callback_function() {        ?>        <script type='text/javascript'>            // jQuery(document).load(function(){                var apikey = '<?php echo $this->apiKey; ?>';                // setTimeout(function(){            // });        </script>        <?php    }}